package com.fahad.controllers;import com.fahad.entityclasses.Contact;import com.fahad.entityclasses.FullUserBean;import com.fahad.entityclasses.User;import com.fahad.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.ArrayList;import java.util.List;@RestControllerpublic class UserController {    @Autowired    private UserService userService;    @PostMapping("/add")    public void addUser(@RequestBody FullUserBean userBean) {        User user = new User();        user.setName(userBean.getName());        user.setUserPassword(userBean.getUserPassword());        List<Contact> contacts = new ArrayList<>();        userBean.getContactsList().stream().forEach(e -> contacts.add(new Contact(e.getContactName(), e.getContactPhoneNumber(), user)));        user.setContactsList(contacts);        userService.addNewUser(user);    }    @GetMapping("/users/{id}")    public List<Contact> getRequest(@PathVariable Long id) {        return userService.getAllContacts(id);    }    @DeleteMapping("/delete/{id}")    public void deleteUser(@PathVariable Long id) {        userService.deleteUser(id);    }}