package com.fahad.entityclasses;import com.fasterxml.jackson.annotation.JsonIgnore;import javax.persistence.*;@Entity@Table(name = "contacts")public class Contact {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String contactName;    private String contactPhoneNumber;    @JsonIgnore    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "user_id", nullable = false)    private User user;    public String getContactName() {        return contactName;    }    public void setContactName(String contactName) {        this.contactName = contactName;    }    public String getContactPhoneNumber() {        return contactPhoneNumber;    }    public void setContactPhoneNumber(String contactPhoneNumber) {        this.contactPhoneNumber = contactPhoneNumber;    }    public User getUser() {        return user;    }    public void setUser(User user) {        this.user = user;    }    public Contact(String contactName, String contactPhoneNumber, User user) {        this.contactName = contactName;        this.contactPhoneNumber = contactPhoneNumber;        this.user = user;    }    public Contact() {    }    @Override    public String toString() {        return "Contact{" +                "id=" + id +                ", contactName='" + contactName + '\'' +                ", contactPhoneNumber='" + contactPhoneNumber + '\'' +                ", user=" + user +                '}';    }}