package com.fahad.entityclasses;import com.fasterxml.jackson.annotation.JsonIgnore;import javax.persistence.*;import java.util.ArrayList;import java.util.List;@Entity@Table(name = "users")public class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String name;    private String userPassword;    @JsonIgnore    @OneToMany(mappedBy = "user", cascade = CascadeType.ALL, orphanRemoval = true)    private List<Contact> contactsList = new ArrayList<>();    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getUserPassword() {        return userPassword;    }    public void setUserPassword(String userPassword) {        this.userPassword = userPassword;    }    public List<Contact> getContactsList() {        return contactsList;    }    public void setContactsList(List<Contact> contactsList) {        this.contactsList = contactsList;    }    @Override    public String toString() {        return "User{" +                "id=" + id +                ", name='" + name + '\'' +                ", userPassword='" + userPassword + '\'' +                ", contactsList=" + contactsList +                '}';    }    public User() {    }}